          <Center>
            <Text
              fontSize={"4xl"}
              fontWeight={400}
              lineHeight={"xs"}
              textAlign={"center"}
            >
              latest Article
            </Text>
          </Center>
        </VStack>
      </Hidden>

      <Hidden till={"md"}>
        <Stack
          marginTop={10}
          justifyContent={"space-between"}
          direction={{ md: "row", sm: "column" }}
          marginLeft={{ md: 200 }}
          marginRight={{ md: 150 }}
        >
          <VStack space={4}>
            <Image
              source={require("../Resources/image-currency.jpg")}
              alt="Alternate Text"
              size={300}
              resizeMode="contain"
            />
            <Text fontSize={"2xl"} fontWeight={400}>
              Online Banking
            </Text>
            <Text textAlign={"left"}>
              our modern web and mobile application allow{"\n"}you to keep track
              of your finances wherever you{"\n"}are in the world.
            </Text>
          </VStack>
          <VStack space={4}>
            <Image
              source={require("../Resources/image-currency.jpg")}
              alt="Alternate Text"
              size={300}
              resizeMode="contain"
            />
